func BenchmarkWordBreak1(b *testing.B) {
	s := ""
	wordDict := []string{"a","aa","aaa","aaaa","aaaaa","aaaaaa","aaaaaaa","aaaaaaaa","aaaaaaaaa","aaaaaaaaaa"}
	for i := 0; i <= b.N; i++ {
		assert.False(b, wordBreak(s, wordDict))
	}
}

